(function() {
  var App;

  module.exports = App = {
    init: function() {}
  };

}).call(this);

(function() {
  var User;

  module.exports = User = (function() {

    function User(name) {
      this.name = name;
    }

    return User;

  })();

  User = require("models/user");

}).call(this);

(function() {
  var App, User;

  User = require("models/user");

  App = {
    init: function() {
      var element, template, view;
      template = require("views/users/show");
      view = template(new User("Brian"));
      element = document.createElement("div");
      element.innerHTML = view;
      return document.body.appendChild(element);
    }
  };

  module.exports = App;

}).call(this);
